package com.methods;

public class parameters_if_else {
	static void checkAge(int num) {
		if(num > 21) {
			System.out.println("Access granted");
		}else {
			System.out.println("Access denied");
		}
			
	}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		checkAge(28);

	}

}
